function App() {
  return (
    <div>
      <h1>
        <span>Quotable Doug</span>
      </h1>
      <dl className="dictionary">{emojipedia.map(emojiTerm=>
  {
    <Entry
      key={emojiTerm.id}
      name={emojiTerm.name}
      description={emojiTerm.meaning}
    />
})}</dl>
    </div>
  );
}

export default App;
